import React, { useState } from 'react'
import ReactDOM from 'react-dom'

const Button = ({onClick, text}) => {
  return (
    <button onClick={onClick}>{text}</button>
  )
}

const Statistics = ({good, neutral, bad}) => {
  let all = 0;
  let average = 0;
  let positive = 0;
  if (good == 0 && neutral == 0 && bad == 0) {
    return (
      <div>
        <h3>Statistics</h3>
        <h4>No feedback given</h4>
      </div>
    )
  }
  all = (good + bad + neutral);
  if (good || bad || neutral) {
    average = (good  + neutral) / all;
    positive = good / all;
  }

  return (
    <div>
      <h3>Statistics</h3>
      // <Display number={good} text='good'/>
      // <Display number={neutral} text='neutral'/>
      // <Display number={bad} text='bad'/>
      // <Display number={all} text='all'/>
      // <Display number={average} text='average'/>
      // <Display number={positive} text='positive'/>
      <table>
        <tr>
          <td>good</td>
          <td>{good}</td>
        </tr>
        <tr>
          <td>neutral</td>
          <td>{neutral}</td>
        </tr>
        <tr>
          <td>bad</td>
          <td>{bad}</td>
        </tr>
        <tr>
          <td>Good</td>
          <td>{good}</td>
        </tr>
        <tr>
          <td>all</td>
          <td>{all}</td>
        </tr>
        <tr>
          <td>average</td>
          <td>{average}</td>
        </tr>
        <tr>
          <td>positive</td>
          <td>{positive}</td>
        </tr>
      </table>
    </div>
  )
}

const Display = ({text, number}) => {
  return (
    <p>{text} {number}</p>
  )
}

const App = () => {
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  const handleGood = () => {
    setGood(good + 1)
  }
  const handleBad = () => {
    setBad(bad +  1)
  }
  const handleNeutral = () => {
    setNeutral(neutral +  1)
  }



  return (
    <div>
      <h3>give feedback</h3>
      <Button onClick={handleGood} text='Good'/>
      <Button onClick={handleNeutral} text='Neutral'/>
      <Button onClick={handleBad} text='Bad'/>

      <Statistics good={good} neutral={neutral} bad={bad}/>
    </div>
  )
}

ReactDOM.render(<App />, document.getElementById('root'))
